import React, { useState, useEffect, useCallback } from 'react';
import { ChevronRightIcon, DocumentIcon, FolderIcon } from '@heroicons/react/24/outline';

// TypeScript æ¥å£å®šä¹‰
interface FileItem {
  name: string;
  type: 'file' | 'directory';
  size?: number;
  lastModified?: Date;
  path: string;
}

interface FileExplorerProps {
  rootPath: string;
  onFileSelect?: (file: FileItem) => void;
  className?: string;
}

// React å‡½æ•°ç»„ä»¶ç¤ºä¾‹
const FileExplorer: React.FC<FileExplorerProps> = ({
  rootPath,
  onFileSelect,
  className = ''
}) => {
  const [files, setFiles] = useState<FileItem[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedFile, setSelectedFile] = useState<FileItem | null>(null);

  // è·å–æ–‡ä»¶åˆ—è¡¨çš„å¼‚æ­¥å‡½æ•°
  const fetchFiles = useCallback(async (path: string) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/files?path=${encodeURIComponent(path)}`);

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      setFiles(data.files || []);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'æœªçŸ¥é”™è¯¯';
      setError(`åŠ è½½æ–‡ä»¶å¤±è´¥: ${errorMessage}`);
      console.error('æ–‡ä»¶åŠ è½½é”™è¯¯:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // ç»„ä»¶æŒ‚è½½æ—¶è·å–æ–‡ä»¶
  useEffect(() => {
    fetchFiles(rootPath);
  }, [rootPath, fetchFiles]);

  // å¤„ç†æ–‡ä»¶é€‰æ‹©
  const handleFileClick = (file: FileItem) => {
    setSelectedFile(file);
    onFileSelect?.(file);
  };

  // æ ¼å¼åŒ–æ–‡ä»¶å¤§å°
  const formatFileSize = (size?: number): string => {
    if (!size) return '-';

    const units = ['B', 'KB', 'MB', 'GB'];
    let unitIndex = 0;
    let fileSize = size;

    while (fileSize >= 1024 && unitIndex < units.length - 1) {
      fileSize /= 1024;
      unitIndex++;
    }

    return `${fileSize.toFixed(1)} ${units[unitIndex]}`;
  };

  // æ¸²æŸ“æ–‡ä»¶é¡¹
  const renderFileItem = (file: FileItem) => {
    const isSelected = selectedFile?.path === file.path;
    const Icon = file.type === 'directory' ? FolderIcon : DocumentIcon;

    return (
      <div
        key={file.path}
        className={`
          flex items-center p-3 cursor-pointer rounded-lg transition-colors
          ${isSelected 
            ? 'bg-blue-100 border-blue-300' 
            : 'hover:bg-gray-50 border-transparent'
          }
          border
        `}
        onClick={() => handleFileClick(file)}
      >
        <Icon className="w-5 h-5 mr-3 text-gray-500" />

        <div className="flex-1 min-w-0">
          <p className="text-sm font-medium text-gray-900 truncate">
            {file.name}
          </p>
          <p className="text-xs text-gray-500">
            {formatFileSize(file.size)} â€¢ {file.lastModified?.toLocaleDateString()}
          </p>
        </div>

        {file.type === 'directory' && (
          <ChevronRightIcon className="w-4 h-4 text-gray-400" />
        )}
      </div>
    );
  };

  return (
    <div className={`file-explorer ${className}`}>
      <div className="mb-4">
        <h2 className="text-lg font-semibold text-gray-900">
          æ–‡ä»¶æµè§ˆå™¨
        </h2>
        <p className="text-sm text-gray-600">
          å½“å‰è·¯å¾„: {rootPath}
        </p>
      </div>

      {loading && (
        <div className="flex items-center justify-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <span className="ml-2 text-gray-600">åŠ è½½ä¸­...</span>
        </div>
      )}

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-4">
          <p className="text-red-800">{error}</p>
          <button
            onClick={() => fetchFiles(rootPath)}
            className="mt-2 text-sm text-red-600 hover:text-red-800 underline"
          >
            é‡è¯•
          </button>
        </div>
      )}

      {!loading && !error && (
        <div className="space-y-1">
          {files.length === 0 ? (
            <p className="text-gray-500 text-center py-8">
              æ­¤æ–‡ä»¶å¤¹ä¸ºç©º
            </p>
          ) : (
            files.map(renderFileItem)
          )}
        </div>
      )}
    </div>
  );
};

