  color: var(--text-color);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.file-meta {
  font-size: 0.75rem;
  color: var(--text-muted);
  margin-top: 0.125rem;
}

/* é¢„è§ˆé¢æ¿ */
.preview-panel {
  width: 400px;
  min-width: 400px;
  border-left: 1px solid var(--border);
  background: var(--background);
  display: flex;
  flex-direction: column;
}

.preview-panel.expanded {
  width: 60%;
  min-width: 600px;
}

.preview-header {
  padding: 1rem;
  border-bottom: 1px solid var(--border);
  background: var(--surface);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.preview-title {
  font-weight: 600;
  font-size: 0.9rem;
  color: var(--text-color);
}

.preview-actions {
  display: flex;
  gap: 0.5rem;
}

.preview-button {
  padding: 0.25rem;
  border: none;
  background: none;
  cursor: pointer;
  border-radius: 4px;
  transition: var(--transition);
}

.preview-button:hover {
  background-color: var(--border);
}

.preview-content {
  flex: 1;
  overflow: auto;
  padding: 1rem;
}

/* ä»£ç é¢„è§ˆ */
.code-preview {
  font-family: 'Monaco', 'Menlo', 'Ubuntu Mono', monospace;
  font-size: 0.875rem;
  line-height: 1.5;
  background: #1e1e1e;
  color: #d4d4d4;
  padding: 1rem;
  border-radius: var(--radius);
  overflow-x: auto;
}

/* å›¾ç‰‡é¢„è§ˆ */
.image-preview {
  text-align: center;
}

.image-preview img {
  max-width: 100%;
  max-height: 70vh;
  border-radius: var(--radius);
  box-shadow: var(--shadow);
}

/* è§†é¢‘é¢„è§ˆ */
.video-preview video {
  width: 100%;
  max-height: 70vh;
  border-radius: var(--radius);
}

/* è¡¨æ ¼é¢„è§ˆ */
.table-preview {
  overflow-x: auto;
}

.table-preview table {
  width: 100%;
  border-collapse: collapse;
  font-size: 0.875rem;
}

.table-preview th,
.table-preview td {
  padding: 0.5rem;
  text-align: left;
  border-bottom: 1px solid var(--border);
}

.table-preview th {
  background: var(--surface);
  font-weight: 600;
  color: var(--text-muted);
}

/* å“åº”å¼è®¾è®¡ */
@media (max-width: 768px) {
  .column {
    min-width: 250px;
    width: 250px;
  }

  .preview-panel {
    width: 100%;
    min-width: 100%;
  }

  .file-browser {
    flex-direction: column;
  }
}

/* æ»šåŠ¨æ¡æ ·å¼ */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--surface);
}

::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--text-muted);
}

/* åŠ è½½åŠ¨ç”» */
.loading {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.spinner {
  width: 24px;
  height: 24px;
  border: 2px solid var(--border);
  border-top: 2px solid var(--primary-color);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}
